import React from 'react';
import * as api from '../services/api';

class CategoryList extends React.Component {
  constructor() {
    super();
    this.state = {
      categories: [],
      loading: true,
      categoryClick: '',
    };

    this.handleClick = this.handleClick.bind(this);
  }

  componentDidMount() {
    this.fetchAPI();
  }

  handleClick(e) {
    this.setState({
      categoryClick: e.innerText,
    });
  }

  async fetchAPI() {
    const { getCategories } = api;
    const categoriesAPI = await getCategories();
    this.setState({
      categories: categoriesAPI,
      loading: false,
    });
  }

  render() {
    const { categories, loading } = this.state;
    if (loading) {
      return (
        <p>Loading...</p>
      );
    }
    return (
      <div>
        <ul>
          { categories.map(
            (cat) => <li data-testid="category" onClick={ this.handleClick } key={ cat.id }>{ cat.name }</li>,
          )}
        </ul>
      </div>
    );
  }
}

export default CategoryList;
