import React from 'react';
import * as api from '../services/api';

class CategoryList extends React {
  constructor() {
    super();
    this.state = {
      categories: {},
    };
  }

  componentDidMount() {
    this.fetchAPI();
  }

  async fetchAPI() {
    const { getCategory } = api;
    const categoriesAPI = await getCategory();
    this.setState({
      categories: categoriesAPI,
    });
  }

  render() {
    return (
      <ul>
        { categories.map((category) => <li data-testid="category" key={ category.id }>{ category.name }</li>) }
      </ul>
    );
  }
}

export default CategoryList;
